(1, 1, 'Mr WordPress', '', 'https://wordpress.org/', '', '2015-11-25 07:51:17', '2015-11-25 07:51:17', 'Hi, this is a comment.\nTo delete a comment, just log in and view the post&#039;s comments. There you will have the option to edit or delete them.', 0, 'post-trashed', '', '', 0, 0),
(2, 1211, 'Krish Purnawarman', 'krishthewiz@gmail.com', 'https://www.krishp.com/', '203.84.141.187', '2015-04-26 05:53:00', '2015-04-26 05:53:00', 'Just Amazing!!,... a paradigm of splendour and tranquility !!', 0, '1', 'Disqus/1.1(2.84):1987902785', '', 0, 0),
(3, 1344, 'melati putri', 'melatiputri02@gmail.com', 'https://plus.google.com/u/0/107707051129274091608/photos', '203.84.141.187', '2015-04-28 10:48:00', '2015-04-28 10:48:00', 'I was curious reading this on another site, but now you''ve got my attention,.. "candlelit path through the jungle,...a Hindu purification ritual to cleanse your soul ?"', 0, '1', 'Disqus/1.1(2.84):1991455089', '', 0, 0);
